import React from 'react';
import Head from 'next/head';
import { Form, Input, Checkbox, Button } from 'antd';
import AppLayout from '../components/AppLayout'

const Signup = () => {
    const [id, setId] = useState('');
    const [nick, setNick] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState('');
    const [term, setTerm]  = useState(false);

    const onSubmit = () => {};
    const onChangeId = () => {};
    const onChangeNick = () => {};
    const onChangePassword = () => {};
    const onChangePasswordChk = () => {};
    const onChangeTerm = () => {};

    return(
        <>
            <Head>
                <title>NodeBird</title>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.25.3/antd.css" />
            </Head>
            <AppLayout>
                <Form onSubmit={onSubmit}>
                    <div>
                        <label htmlFor="user-id">아이디</label> <br />
                        <Input name-="user-id" required onChange={onChangeId} />
                    </div>
                    <div>
                        <label htmlFor="user-nick">별명</label> <br />
                        <Input name-="user-nick" required onChange={onChangeNick} />
                    </div>
                    <div>
                        <label htmlFor="user-pass">비밀번호</label> <br />
                        <Input name-="user-pass" type="password" required onChange={onChangePassword} />
                    </div>
                    <div>
                        <label htmlFor="user-pass-chk">비밀번호 확인</label> <br />
                        <Input name-="user-pass-chk" type="password" required onChange={onChangePasswordChk} />
                    </div>
                    <div>
                        <Checkbox name="user-term" onChange={onChangeTerm}>
                            제로초 말을 잘 들을 것을 동의합니다.
                        </Checkbox>
                    </div>
                    <div>
                        <Button type="primary" htmlType="submit">가입하기</Button>
                    </div>
                </Form>
            </AppLayout>
        </>
    );
}

export default Signup;
