//state
export const initialState = {
    isLoggedIn: false,
    user: {},
};

//actions
const LOG_IN = 'LOG_IN'; //액션의 이름
export const LOG_OUT = 'LOG_OUT';

const loginAction = {
    type: LOG_IN,
    data: {
        nickname: '제로초',
    }
};
const logoutAction = {
    type: LOG_OUT,
};

const reducer = (state = initialState, action) => {
    //다음 state를 만듦
    switch (action.type) {
        case loginAction: {
            return {
                ...state, //새로운 객체를 생성, 참조가 달라져서 state값이 달라졌구나 알게됨
                isLoggedIn: true,
                user: action.data
            }
        }
        case logoutAction: {
            return{
                ...state,
                isLoggedIn: false,
                user: null,
            }
        }
        default: {
            return {
                ...state,
            }
        }
    }
};

export default reducer;
