import { all, fork, takeLatest, takeEvery, call, put, take, delay } from 'redux-saga/effects';
import { LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAIURE } from '../reducers/user';

const HELLO_SAGA = 'HELLO_SAGA';

function* loginAPI() {
    //서버에 요청을 보내는 부분
}

function* login() {
    try{
        yield delay(4);
        yield put({//put은 dispatch와 동일
            type: LOG_IN_SUCCESS
        });
    } catch (e) {//loginAPI 실패
        console.error(e);
        yield put({ 
            type: LOG_IN_FAIURE,
        });
    }
}

function* watchLogin() {
    while (true) {
        yield take(LOG_IN);
        delay(2000);
        yield put({
            type: LOG_IN_SUCCESS,
        });
    }
};


export default function* userSaga() {
    yield all([
        watchLogin(),
        watchSignUp(),
    ]);
};
