import { all, fork, akeLatest, call, put } from 'redux-saga/effects';
import { LOG_OUT, LOG_IN_SUCCESS, LOG_IN_FAIURE } from '../reducers/user';

function* loginAPI() {
    
}

function* login() {
    try{
        yield delay(4);
        yield put({//put은 dispatch와 동일
            type: LOG_IN_SUCCESS
        });
    } catch (e) {//loginAPI 실패
        console.error(e);
        yield put({ 
            type: LOG_IN_FAIURE,
        });
    }
}

function* watchLogin() {
    yield takeLatest(LOG_IN, login)
}

function* helloSaga() {
    console.log('hello saga');
    yield take(HELLO_SAGA);
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        helloSaga(),
    ]);
};
