import React from 'react';
import { Form, Input, Button } from 'antd';

const dummy = {
    isLoggedIn: false,
    imagePaths: [],
    mainPosts: [{
        User: {
            id: 'id몽땅',
            nickname: '몽땅이',
        },
        content: 'Movie : About Time',
        img: 'https://i.pinimg.com/originals/01/78/9c/01789c4656b4348a2e501089288eeb51.jpg',
    }],
};

const PostForm = () => {
    return(
        <Form style={{margin:'30px'}} encType='multipart/form-data'>
            <Input.TextArea maxLength={140} placeholder='set me up!!' />
            <div>
                <input type='file' multiple hidden />
                <Button>이미지 업로드</Button>
                <Button type='primary' style={{float:'right'}} htmlFor='submit'>트윗</Button>
            </div>
            <div>
                {dummy.imagePaths.map( (v, i) => {
                    return(
                        <div key={v} style={{display:'inline-bloick'}}>
                        <img src={'http://localhost:3065' + v} style={{width:'200px'}} alt={v} />
                        <div>
                            <Button>제거</Button>
                        </div>
                        </div>  
                    );
                } )}
            </div>
        </Form>
    );
}

export default PostForm;
