import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Input, Button, Row, Col, Card, Avatar, Form } from 'antd';

// 가짜 데이터 만들기, 서버로부터 받을 예상 데이터를 만들어서 작업을 한다.
const dummy = {
    nickname: '제로초',
    desc: 'next.js 정복하고싶어요!!',
    Post: [],
    Following: [],
    Follower: [],
}

const AppLayout = ({ children }) => {
    return(
        <div>
            <Menu mode={"horizontal"}>
                <Menu.Item key="home">
                    <Link href="/"><a>노드버드</a></Link>
                    {/* Link와 a 태그는 한 쌍 */}
                </Menu.Item>
                <Menu.Item key="profile">
                    <Link href="/profile"><a>프로필</a></Link>
                </Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={ {verticalAlign:'middle'} } />
                </Menu.Item>
            </Menu>
            <Link href="/signup"><a><Button>회원가입</Button></a></Link>
            <Row>
                {/* xs:모바일, sm:작은화면, md:중간화면, lg:큰화면 (가로너비 기준) */}
                <Col xs={24} md={6}>
                    {/* 로그인되었을 경우 */}
                    <Card
                        actions={[
                            <div key="twit">트위터<br />{dummy.Post.length}</div>,
                            <div key="following">팔로잉<br />{dummy.Following.length}</div>,
                            <div key="follower">팔로워<br />{dummy.Follower.length}</div>
                        ]}
                    >
                        {/* card.meta: card에 대한 정보를 입력하는 칸 */}
                        <Card.Meta
                            avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                            title={dummy.nickname}
                            description={dummy.desc}
                        />
                    </Card>
                    {/* 로그아웃되었을 경우 */}
                    <Form>
                        <div>
                            <label htmlFor="user-id">아이디</label>
                            <br />
                            <Input name="user-id" value={userId} onChange={onChangId} required />
                        </div>
                        <div>
                            <label htmlFor="user-password">비밀번호</label>
                            <br />
                            <Input name="user-password" type="password" value={password} onChange={onChangPassword} required />
                        </div>
                        <div>
                            <Button type="primary" htmlFor="submit" loading={false}>로그인</Button>
                        </div>
                    </Form>
                </Col>
                <Col xs={24} md={12}>
                    {children}
                </Col>
                <Col xs={24} md={6}>

                </Col>
            </Row>
        </div>
    );
};

AppLayout.propTypes = {
    // 렌더링될 수 있는 아이들은 다 node
    children: PropTypes.node,
}

export default AppLayout;
