//메인 화면
import React from 'react';
import { Form, Input, Button } from 'antd';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [],
};

const Home = () => {
    return(
        <div>
            {dummy.isLoggedIn && <Form encType='multipart/form-data'>
                <Input.TextArea maxLength={140} placeholder='set me up!!' />
                <div>
                    <input type='file' multiple hidden />
                    <Button>이미지 업로드</Button>
                    <Button type='primary' style={{float:'right'}} htmlFor='submit'>트윗</Button>
                </div>
                <div>
                    {dummy.imagePaths.map( (v, i) => {
                        return(
                          <div key={v} style={{display:'inline-bloick'}}>
                            <img src={'http://localhost:3065' + v} style={{width:'200px'}} alt={v} />
                            <div>
                                <Button>제거</Button>
                            </div>
                          </div>  
                        );
                    } )}
                </div>
            </Form>}
            {dummy.mainPosts.map( (c) => {
                return(
                    <Card
                        key={c.createdAt}
                        cover={c.img && <img alt='example' src={c.img} />}
                    >

                    </Card>
                );
            } )}
        </div>
    );
};

export default Home;
