//     }],
// };

const Home = ({}) => {
    const { user, isLoggedIn } = useSelector(state => state.user);
    const { mainPosts } = useSelector(state => state.post);
    console.log({user});

    return(
        <div>
            {/* {user ? <div>로그인 { user.nickname }</div> : <div>로그아웃</div>} */}
            {isLoggedIn && <PostForm />}
            {mainPosts.map( (c) => {
                return(
                    <PostCard key={c} post={c} />
                );
            } )}
        </div>
    );
};

export default Home;
