import { all } from 'redux-saga/effects';
import { LOG_OUT, LOG_IN_SUCCESS, LOG_IN_FAIURE } from '../reducers/user';

function* loginAPI() {
    
}

function* login() {
    try{
        yield call(loginAPI);
        yield put({//put은 dispatch와 동일
            type: LOG_IN_SUCCESS
        });
    } catch (e) {//loginAPI 실패
        console.error(e);
        yield put({ 
            type: LOG_IN_FAIURE,
        });
    }
}

function* watchLogin() {
    yield takeLatest(LOG_IN, login)
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
    ]);
};
