import { all, fork, akeLatest, call, put, take } from 'redux-saga/effects';
import { LOG_OUT, LOG_IN_SUCCESS, LOG_IN_FAIURE } from '../reducers/user';

const HELLO_SAGA = 'HELLO_SAGA';

function* loginAPI() {
    //서버에 요청을 보내는 부분
}

function* login() {
    try{
        yield delay(4);
        yield put({//put은 dispatch와 동일
            type: LOG_IN_SUCCESS
        });
    } catch (e) {//loginAPI 실패
        console.error(e);
        yield put({ 
            type: LOG_IN_FAIURE,
        });
    }
}

function* watchLogin() {
    yield takeLatest(LOG_IN, login)
};

function* helloSaga() {
    while (true) {
        yield take(HELLO_SAGA);
        console.log('hello saga');
        //비동기 요청, 타이머 넣어도 됨
    }
};

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        helloSaga(),
    ]);
};
