//     }],
// };

const Home = ({}) => {
  // const { isLoggedIn } = useSelector(state => state.user);
  const { mainPosts } = useSelector((state) => state.post);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch({
      type: "LOG_IN_REQUEST",
    });
  }, []);

  return (
    <div>
      {isLoggedIn && <PostForm />}
      {mainPosts.map((c) => {
        return <PostCard key={c} post={c} />;
      })}
    </div>
  );
};

function* generator() {}

export default Home;
